from bs4 import BeautifulSoupimport requestsimport rekeyword=input('Input your keyword for pictures:')default_link='https://wall.alphacoders.com/big.php?i='pattern=re.compile(r'\d+')pic_links=[]for n in range(5):    url='https://wall.alphacoders.com/search.php?search=%s'%keyword+'&page=%d'%n    html=requests.get(url).text    soup=BeautifulSoup(html,'lxml')    links=soup.find_all('img')    img_urls = []    for i in links:        img_urls.append(i['src'])    img_urls=[i for i in img_urls if i.startswith('https://images')]    for i in img_urls:        result=pattern.findall(i.split('-')[-1])        result=[default_link+s for s in result]        result=str(result[0])        pic_links.append(result)print('There are %d picturs waiting for download.'%len(pic_links))