import pandas as pdfrom sqlalchemy import create_enginedef extraction():    host='localhost'    dbname='postgres'    user='postgres'    password='wenqian628'    engine = create_engine('postgresql://postgres:wenqian628@localhost:5432/postgres')    sql='''    select aapl.date,aapl.close as aapl,fb.close as fb,fdx.close as fdx,googl.close as googl,msft.close as msft,tmus.close as tmus,twtr.close as twtr     from aapl     left join fb on aapl.date=fb.date    left join fdx on fdx.date=aapl.date     left join googl on aapl.date=googl.date    left join msft on aapl.date=msft.date     left join tmus on aapl.date=tmus.date    left join twtr on aapl.date=twtr.date    '''    df=pd.read_sql_query(sql=sql,con=engine)    df['date']=pd.to_datetime(df.date)    df.set_index('date',inplace=True)    return df